public static void sayHelloWithVoice() throws Exception {
    Runtime.getRuntime().exec("cmd /c start mshta vbscript:Execute(\"CreateObject('SAPI.SpVoice').Speak('سلام شایان! من فعال شدم')(window.close)\")");
}

public static void openFolder(String path) throws Exception {
    Runtime.getRuntime().exec("explorer.exe " + path);
}

public static void openWebsite(String url) throws Exception {
    java.awt.Desktop.getDesktop().browse(new java.net.URI(url));
}

public static void writeToFile(String path, String content) throws Exception {
    java.nio.file.Files.writeString(java.nio.file.Path.of(path), content);
    System.out.println("✅ نوشته شد: " + path);
}

public static void createFile(String name, String ext) throws Exception {
    java.io.File file = new java.io.File(name + "." + ext);
    if (file.createNewFile())
        System.out.println("✅ ساخته شد: " + file.getAbsolutePath());
    else
        System.out.println("⚠️ فایل از قبل وجود دارد.");
}

public static void launchByName(String name) throws Exception {
    String cmd = switch (name.toLowerCase()) {
        case "notepad" -> "notepad.exe";
        case "calculator", "calc" -> "calc.exe";
        default -> name + ".exe";
    };
    Runtime.getRuntime().exec(cmd);
}

public static void startCountdown(int seconds) throws Exception {
    System.out.println("⏳ تایمر شروع شد: " + seconds + " ثانیه");
    Thread.sleep(seconds * 1000);
    Runtime.getRuntime().exec("cmd /c start mshta vbscript:Execute(\"CreateObject('SAPI.SpVoice').Speak('زمان تموم شد')(window.close)\")");
}

public static void setReminder(String timeHHMM, String message) throws Exception {
    String script = "Set oShell = CreateObject("WScript.Shell")
Do While True
If Time = "" + timeHHMM + "" Then
oShell.Run "mshta vbscript:Execute("CreateObject('SAPI.SpVoice').Speak('" + message + "')(window.close)")"
Exit Do
End If
WScript.Sleep 1000
Loop";
    java.nio.file.Files.writeString(java.nio.file.Path.of("reminder.vbs"), script);
    Runtime.getRuntime().exec("wscript reminder.vbs");
}

public static void searchFile(String filename, String directory) {
    java.io.File dir = new java.io.File(directory);
    java.io.File[] files = dir.listFiles((d, name) -> name.equalsIgnoreCase(filename));
    if (files != null && files.length > 0)
        System.out.println("📁 پیدا شد: " + files[0].getAbsolutePath());
    else
        System.out.println("🔍 پیدا نشد.");
}

public static void runCommand(String cmd) throws Exception {
    Runtime.getRuntime().exec("cmd /c " + cmd);
}
